import React from 'react';
import './Chatbot.css';

function Chatbot() {
  const chats = [
    { id: 1, name: 'John Doe' },
    { id: 2, name: 'Jane Doe' },
    { id: 3, name: 'Bob Smith' },
    { id: 4, name: 'Alice Johnson' },
  ];

  const messages = [
    { id: 1, message: 'Hello!', sender: 'me' },
    { id: 2, message: 'How are you?', sender: 'them' },
    { id: 3, message: 'I am doing well, thank you for asking.', sender: 'me' },
    { id: 4, message: 'That is great to hear!', sender: 'them' },
  ];

  return (
    <div className="messaging-screen">
        <div className='bg-grey'>
            <div className="chat-list">
                <h3>Chats</h3>
                <ul>
                {chats.map(chat => (
                    <li key={chat.id}>
                    <a href="#">{chat.name}</a>
                    </li>
                ))}
                </ul>
            </div>
            <div className="message-container">
                <div className="message-list">
                {messages.map(message => (
                    <div
                    key={message.id}
                    className={`message ${message.sender === 'me' ? 'sent' : 'received'}`}
                    >
                    <p>{message.message}</p>
                    </div>
                ))}
                </div>
            </div>
        </div>
    </div>
  );
}

export default Chatbot;
