import React, {useState, useEffect} from "react";
import Footer from "./components/Footer/Footer";
import Homepage from "./components/Homepage/Homepage";
import Recieved from "./components/Homepage/Recieved";

import TopNav from "./components/TopNav/TopNav";
import axios from 'axios';
import './static/style.css'
function App() {
  const [status, setStatus] = useState(0)
  const [data, setData] = useState(        <div class="split">
  <div class="filled">

    <h1 id="list">'shit the fsadfsf' translates to:</h1>


    <p class="signlanguage beside"> shit </p>



    <p class="signlanguage beside"> the </p>



    <p class="signlanguage beside"> fsadfsf </p>



  </div>
</div>)

  useEffect(() => {
    const intervalId = setInterval(() => {
      // console.log("Checking .....")

      axios.get('http://100.65.108.182:8000/getresponse')
        .then(response => {
          console.log(data)
          if (response.data !== "" || response.data != undefined) {
            setData(data)
            setStatus(1)
          }
        })
        .catch(error => console.log(error));  

      axios.get('http://100.66.64.140:3000/reset')
        .then(response => {
          console.log(response)
          if (response.data == true) {
            setStatus(0)
          }
        })
        .catch(error => console.log(error));  
    }, 1000);

    return () => clearInterval(intervalId);
  }, [])

  return (
    <>
      <TopNav />
      {/* {status === 0 && <Homepage />} */}
      {/* {status === 1 && <Recieved data={data}/>} */}
      <Recieved />

      <Footer />
    </>
  );
}

export default App;
