import './style.css'

const LandingPage = () => {
    return(
        <div id="hero">
            <div className="columns">
                <div className="column">
                    <h2 style="max-width: 500px;">The bridge between <span style="color: #E29A0A"> sign language </span>and the world</h2>
                    <p style="margin-bottom: 10%; max-width: 500px; font-size: 1.2rem; margin-top: 7%;"><i>Signify</i> is a revolutionary camera vision-based app designed to help bridge the communication gap between the deaf community and hearing individuals. </p>
                    <a href="#demo" className="cta" style="background: radial-gradient(306% 4024.8% at 143.46% -72%, #E7A310 0%, #C88705 55.6%, #E7A310 100%);
                    border-radius: 6px;">View the Platform</a>
            </div>
            <div class="column" style="text-align: right;">
                <img src="Assets/Group 15.png" style="width: 100%; height: 100%; max-width: 580px; bottom:0; top: 0; right: 0" />
            </div>
        </div>
      </div>
    );
}

export default LandingPage;
