import React from 'react';
import { Form, FormControl, InputGroup } from 'react-bootstrap';
import './chatBox.css';

const chatHistory = [
  { id: 1, text: 'Hi there!', sender: 'user' },
  { id: 2, text: 'Hello!', sender: 'bot' },
  { id: 3, text: 'How are you?', sender: 'bot' },
  { id: 4, text: 'I\'m good, thanks!', sender: 'user' },
  { id: 5, text: 'What can I help you with today?', sender: 'bot' },
  // ...
];

function Chatbox() {
  return (
    <div className="chat-box">
      <div className="chat-history">
        {chatHistory.map((message) => (
          <div key={message.id} className={message.sender === 'user' ? 'message-user' : 'message-bot'}>
            <span>{message.text}</span>
          </div>
        ))}
      </div>
      <InputGroup className="mb-3">
        <FormControl placeholder="Search messages" />
      </InputGroup>
    </div>
  );
}

export default Chatbot;
